import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as LogoSVG } from '../assets/graphics/logo.svg';


const Nav = styled.nav`
  background: #FFFFFA;
  color: #121212;
  padding: 1rem;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: fixed;
  width: 97%;
  z-index: 1000;
`;

const NavList = styled.ul`
  list-style: none;
  padding: 0;
  text-align: right;
  margin-right: 1rem;
`;

const NavItem = styled.li`
  display: inline;
  margin: 0 1rem;
  font-size: 1.5rem;
  font-weight: bold;
`;

const NavLink = styled(Link)`
  color: #121212;
  text-decoration: none;
`;

const Logo = styled(Link)`
    margin-left: 1rem;
    svg {
        width: 150px;
        height: 70px;
    }
`;

const LinkedinLogo =  styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }
`;

const GithubLogo = styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }           
`;

const Header = ({ onNavigate }) => {
  return (
    <Nav>
        <Logo onClick={() => onNavigate('home')}>
            <LogoSVG />
        </Logo>
        <NavList>
            <NavItem><NavLink onClick={() => onNavigate('home')}>Work</NavLink></NavItem>
            <NavItem><NavLink onClick={() => onNavigate('home')}>About</NavLink></NavItem>
        </NavList>
    </Nav>
  );
};

export default Header;
