import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as LinkedinSVG } from '../assets/graphics/linkedin.svg';
import { ReactComponent as GithubSVG } from '../assets/graphics/github.svg';
import { motion } from 'framer-motion';

const FooterContainer = styled.footer`
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexDirection: 'column',
    background: #FFFFFA;
    color: #121212;
    padding: 2rem 1rem;
    bottom: 0;
    max-width: 100%;
    margin-left: 0.5rem;
    margin-right: 0.5rem;
`;

const FooterContent = styled.div`
    margin: 1rem auto;
    margin-left: 1rem;
    margin-right: 1rem;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    gap: 50rem;
    max-width: 100%;

    @media (max-width: 768px) {
    flex-direction: row;
    justify-content: space-between;
    }
`;

const TopRow = styled.div`
  display: flex;
  font-size: 1.5rem;
  font-weight: medium;
`;

const BottomRow = styled.div`
  display: inline;
  font-size: 1.5rem;
  font-weight: medium;
`;

const FooterItem = styled.div`
  display: inline;
  font-size: 1.5rem;
  font-weight: medium;
`;

const LinkedinLogo =  styled(motion.a)`
    svg {
        width: 1.5rem;
        height: 1.5rem;
    }
`;

const GithubLogo = styled(motion.a)`
    svg {
        width: 1.5rem;
        height: 1.5rem;
    }           
`;

const LogoContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 2rem;
`;

const ButtonContainer = styled.div`
    display: flex;
    flex-direction: row;
    margin-top: 1rem;
    align-items: center;
    margin: 0 auto;
    gap: 10rem;

    @media (max-width: 768px) {
        flex-direction: column; /* Change flex direction for tablets and smaller screens */
        align-items: center;
        gap: 2rem;
    }
`;

const DownloadButton = styled(motion.a)`
  font-size: 1rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 0.5rem;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const EmailButton = styled(motion.a)`
  font-size: 1rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 0.5rem;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const Footer = () => {
  return (
    <FooterContainer>
        <TopRow>
            <ButtonContainer id="button-section">
                <DownloadButton
                    href="/assets/Documents/GiacomoLisitaCV_CVE_EN.pdf"
                    download="GiacomoLisitaCV.pdf"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                >
                    Download Resume;
                </DownloadButton>
                <EmailButton
                    href="mailto:giacomolisita01@gmail.com"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    >
                    Email me;
                </EmailButton>
            </ButtonContainer>
        </TopRow>
        <BottomRow>
            <FooterItem>
                <p>&copy; 2024 Giacomo Lisita</p>
            </FooterItem>
            <LogoContainer>
                <LinkedinLogo
                    href="https://it.linkedin.com/in/giacomolisita"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                >
                    <LinkedinSVG />
                </LinkedinLogo>
                <GithubLogo 
                    href = "https://github.com/giacolees"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                >
                    <GithubSVG />
                </GithubLogo>
            </LogoContainer>
        </BottomRow>
    </FooterContainer>
  );
};

export default Footer;
