import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: top;
  height: 1000vh;
  background: #FFFFFA;
  color: #333;
`;

const TypingText = styled(motion.h1)`
  font-size: 4rem;
  margin-top: 20rem;
  border-right: 0.1em solid #333;
  white-space: nowrap;
  overflow: hidden;
  font-family: 'Source+Code+Pro', Source Code Pro;
`;

const Subtitle = styled(motion.p)`
  font-size: 1.5rem;
  margin-bottom: 10rem;
  color: #666;
`;

const DownloadButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1rem;
  color: #fff;
  background: #28a745;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
`;

const Work = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = "\"HHello;\"";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length - 1) {
        setDisplayedText(prev => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust the speed of typing here

    return () => clearInterval(interval);
  }, []);

  return (
    <HomeSection>
      <TypingText
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {displayedText}
      </TypingText>
      <Subtitle
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 2 }}
      >
        Iâ€™m Giacomo, a young computer engineer 
      </Subtitle>
      <DownloadButton
        href="/assets/Documents/GiacomoLisitaCV_CVE_EN.pdf"
        download="GiacomoLisitaCV.pdf"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, delay: 3.5 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        Download Resume;
      </DownloadButton>
    </HomeSection>
  );
};

export default Work;
