import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as LogoSVG } from '../assets/graphics/logo.svg';
import { ReactComponent as LinkedinSVG } from '../assets/graphics/linkedin.svg';
import { ReactComponent as GithubSVG } from '../assets/graphics/github.svg';

const Nav = styled.nav`
  background: #FFFFFA;
  color: #333;
  padding: 1rem;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: fixed;
  width: 97%;
  z-index: 1000;
`;

const NavList = styled.ul`
  list-style: none;
  padding: 0;
  text-align: right;
`;

const NavItem = styled.li`
  display: inline;
  margin: 0 1rem;
  font-size: 1.2rem;
  font-weight: bold;
`;

const NavLink = styled(Link)`
  color: #000;
  text-decoration: none;
`;

const Logo = styled(Link)`
    margin-left: 1rem;
    svg {
        width: 200px;
        height: 100px;
    }
`;

const LinkedinLogo =  styled(Link)`
    svg {
        width: 50px;
        height: 50px;
    }
`;

const GithubLogo = styled(Link)`
    svg {
        width: 50px;
        height: 50px;
    }           
`;

const Header = () => {
  return (
    <Nav>
        <Logo to="/">
            <LogoSVG />
        </Logo>
        <NavList>
            <NavItem><NavLink to="/">Work</NavLink></NavItem>
            <NavItem><NavLink to="/About">About</NavLink></NavItem>
            <NavItem>
                <LinkedinLogo to="https://it.linkedin.com/in/giacomolisita">
                    <LinkedinSVG />
                </LinkedinLogo>
            </NavItem>
            <NavItem>   
                <GithubLogo to="https://github.com/giacolees">
                    <GithubSVG />
                </GithubLogo>
            </NavItem>
        </NavList>

    </Nav>
  );
};

export default Header;
