import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useLocation } from 'react-router-dom';


const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: top;
  background: #FFFFFA;
  color: #121212;
  max-width: 100%;

`;

const TypingText = styled(motion.h1)`
  font-size: 4rem;
  margin-top: 8rem;
  border-right: 0.1em solid #121212;
  white-space: nowrap;
  overflow: hidden;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 900;
  max-width: 100%;
`;

const Subtitle = styled(motion.p)`
  font-size: 2.0rem;
  color: #121212;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 500;
  text-align: center;
  padding-bottom: 9rem;

  @media (max-width: 768px) {
    font-size: 1.5rem; 
    margin-top: 2rem;
    max-width: 70%;
  }

`;

const MainPageContent = styled.div`
  width: 100%;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  text-align: center;
  gap: 10rem;
  justify-content: center;
  margin-top: 10rem;
  width: 100%;
`;

const ProjectsSection = styled.div`
  flex: 1 1 calc(33.333%);
  box-sizing: border-box;
  gap: 5rem;
  justify-content: top;
  margin-top: 1rem;
  margin-bottom: 1rem;
  align-items: center;
  background: #FFFFFA;
  color: #121212;
  border-radius: 5px;
  padding: 2rem;
  border: 1px solid #121212;

   @media (max-width: 768px) {
    flex: 1 1 calc(50% - 40px); /* Two columns on tablets and small screens */
  }

  @media (max-width: 480px) {
    flex: 1 1 100%; /* One column on mobile devices */
  }
`;

const LearnMoreButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const ExploreProjButt = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #B00020;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
  margin-bottom: 1rem;
`;

const Works = styled(motion.p)`
  font-size: 2.0rem;
  color: #121212;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 500;
  text-align: center;
  padding-bottom: 9rem;

  @media (max-width: 768px) {
    font-size: 1.5rem; 
    margin-top: 2rem;
    max-width: 70%;
  }

`;

const Work = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = "\"HHello\"";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length - 1) {
        setDisplayedText(prev => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust the speed of typing here

    return () => clearInterval(interval);
  }, []);

  const projectsContainerRef = useRef(null);

  const scrollIntoView = () => {
    projectsContainerRef.current.scrollIntoView({ behavior: 'smooth' });
  }


  return (
    <HomeSection>
      <MainPageContent>

      </MainPageContent>

      <ProjectsContainer ref={projectsContainerRef}>
        <ProjectsSection>
        </ProjectsSection>
      </ProjectsContainer>
    </HomeSection>
  );
};

export default Work;
