import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';


const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: top;
  min-height: 100vh;
  background: #FFFFFA;
  color: #121212;
  max-width: 100%;
`;

const TypingText = styled(motion.h1)`
  font-size: 4rem;
  margin-top: 8rem;
  border-right: 0.1em solid #121212;
  white-space: nowrap;
  overflow: hidden;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 900;
`;

const Subtitle = styled(motion.p)`
  font-size: 2.0rem;
  color: #121212;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 500;
  margin-bottom: 10rem;
  text-align: center;
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: row;
  fle
  gap: 1rem;
  margin-top: 1rem;
  flex-container {
  display: flex;
  flex-direction: row; /* Default flex direction */
  flex-wrap: wrap;
  gap: 20px; /* Space between items */
  }

  @media (max-width: 768px) {
  flex-container {
    flex-direction: column; /* Change flex direction for tablets and smaller screens */
  }
  }
`;

const DownloadButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
  flex-item {
  flex: 1 1 200px; /* Flex-grow, flex-shrink, flex-basis */
  height: 150px;
  background-color: lightblue;
  border: 1px solid #ddd;
  }
`;

const EmailButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const ProjectsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  text-align: center;
  gap: 10rem;
  justify-content: center;
  margin-top: 10rem;
  width: 100%;
`;

const ProjectsSection = styled.div`
  flex: 1 1 calc(33.333%);
  box-sizing: border-box;
  gap: 5rem;
  justify-content: top;
  margin-top: 1rem;
  margin-bottom: 1rem;
  align-items: center;
  background: #FFFFFA;
  color: #121212;
  border-radius: 5px;
  padding: 2rem;
  border: 1px solid #121212;

   @media (max-width: 768px) {
    flex: 1 1 calc(50% - 40px); /* Two columns on tablets and small screens */
  }

  @media (max-width: 480px) {
    flex: 1 1 100%; /* One column on mobile devices */
  }
`;

const ProjectText = styled.li`
  display: inline;
  margin: 0 1rem;
  font-size: 1.5rem;
  font-weight: bold;
`;


const LearnMoreButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const Work = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = "\"HHello;\"";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length - 1) {
        setDisplayedText(prev => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust the speed of typing here

    return () => clearInterval(interval);
  }, []);

  return (
    <HomeSection>
      <TypingText
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {displayedText}
      </TypingText>
      <Subtitle
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 1 }}
      >
        I’m Giacomo and this is my portfolio ;)
      </Subtitle>
      <ButtonContainer id="button-section">
      <DownloadButton
        href="/assets/Documents/GiacomoLisitaCV_CVE_EN.pdf"
        download="GiacomoLisitaCV.pdf"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, delay: 1.5 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        Download Resume;
      </DownloadButton>
      <EmailButton
          href="mailto:giacomolisita01@gmail.com" // Replace with your email
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 1.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Email me;
      </EmailButton>
      </ButtonContainer>
      <ProjectsContainer>
        Work
          <ProjectsSection>
            <LearnMoreButton
              href="/projects"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 1, delay: 1.5 }}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              Learn more about my projects
            </LearnMoreButton>
        </ProjectsSection>
      </ProjectsContainer>
    </HomeSection>
  );
};

export default Work;
