import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as LinkedinSVG } from '../assets/graphics/linkedin.svg';
import { ReactComponent as GithubSVG } from '../assets/graphics/github.svg';
import { motion } from 'framer-motion';

const FooterContainer = styled.footer`
  background: #FFFFFA;
  color: #121212;
  padding: 2rem 1rem;
  text-align: center;
  position: relative;
  bottom: 0;
  max-width: 100%;
  max-height: 100%;
  margin-left: 1rem;
 margin-right: 1rem;
`;

const FooterContent = styled.div`
  max-width:  100%;
  margin: 1rem auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  @media (min-width: 768px) {
    flex-direction: row;
    justify-content: space-between;
  }
`;

const FooterSection = styled.div`
    margin: 1rem 0;
    display: flex;
    font-size: 1.5rem;
`;

const FooterItem = styled.li`
  display: inline;
  margin: 0 1rem;
  font-size: 1.5rem;
  font-weight: bold;
`;

const LinkedinLogo =  styled(Link)`
    svg {
        width: 50px;
        height: 50px;
    }
`;

const GithubLogo = styled(Link)`
    svg {
        width: 50px;
        height: 50px;
    }           
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 1rem;
  margin-top: 1rem;
  max-width: 80%;

  @media (max-width: 768px) {
    flex-direction: column; /* Change flex direction for tablets and smaller screens */
    text-align: center;
  }
`;

const DownloadButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 0.5rem;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const EmailButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 0.5rem;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const Footer = () => {
  return (
    <FooterContainer>
        <FooterContent>
            <ButtonContainer id="button-section">
                <DownloadButton
                    href="/assets/Documents/GiacomoLisitaCV_CVE_EN.pdf"
                    download="GiacomoLisitaCV.pdf"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                >
                    Download Resume;
                </DownloadButton>
                <EmailButton
                    href="mailto:giacomolisita01@gmail.com"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    >
                    Email me;
                </EmailButton>
            </ButtonContainer>
            <FooterItem>
                <p>&copy; 2024 Giacomo Lisita</p>
            </FooterItem>
                <FooterItem>
                    <LinkedinLogo to="https://it.linkedin.com/in/giacomolisita">
                        <LinkedinSVG />
                    </LinkedinLogo>
                </FooterItem>
                <FooterItem>
                    <GithubLogo to="https://github.com/giacolees">
                        <GithubSVG />
                    </GithubLogo>
                </FooterItem>
        </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
