import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: top;
  height: 1000vh;
  background: #FFFFFA;
  color: #333;
`;

const TypingText = styled(motion.h1)`
  font-size: 4rem;
  margin-top: 20rem;
  border-right: 0.1em solid #333;
  white-space: nowrap;
  overflow: hidden;
  font-family: 'Source+Code+Pro', Source Code Pro;
`;

const Subtitle = styled(motion.p)`
  font-size: 1.5rem;
  margin-bottom: 10rem;
  color: #666;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const DownloadButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1rem;
  color: #000;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  &:hover {
    background: #B00020;
  }
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const EmailButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1rem;
  color: #000;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  &:hover {
    background: #B00020;
  }
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const Work = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = "\"HHello;\"";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length - 1) {
        setDisplayedText(prev => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust the speed of typing here

    return () => clearInterval(interval);
  }, []);

  return (
    <HomeSection>
      <TypingText
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {displayedText}
      </TypingText>
      <Subtitle
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 1.5 }}
      >
        Iâ€™m Giacomo, a young computer engineer 
      </Subtitle>
      <ButtonContainer id="button-section">

      </ButtonContainer>

    </HomeSection>
  );
};

export default Work;
