import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HomeSection = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: top;
  height: 1000vh;
  background: #FFFFFA;
  color: #121212;
`;

const TypingText = styled(motion.h1)`
  font-size: 6rem;
  margin-top: 10rem;
  border-right: 0.1em solid #121212;
  white-space: nowrap;
  overflow: hidden;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: 900;
`;

const Subtitle = styled(motion.p)`
  font-size: 3.5rem;
  margin-bottom: 0.5rem;
  color: #121212;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;
const SubSubtitle = styled(motion.p)`
  font-size: 5rem;
  margin-bottom: 10rem;
  color: #121212;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const DownloadButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const EmailButton = styled(motion.a)`
  padding: 1rem 2rem;
  font-size: 1.8rem;
  color: #121212;
  background: #FFFFFA;
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  font-family: 'Source+Code+Pro', Source Code Pro;
  font-weight: regular;
`;

const WorkSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
`;

const Work = () => {
  const [displayedText, setDisplayedText] = useState('');
  const fullText = "\"HHello;\"";

  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      if (index < fullText.length - 1) {
        setDisplayedText(prev => prev + fullText[index]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust the speed of typing here

    return () => clearInterval(interval);
  }, []);

  return (
    <HomeSection>
      <TypingText
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {displayedText}
      </TypingText>
      <Subtitle
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 1 }}
      >
        Iâ€™m Giacomo
      </Subtitle>
      <Subtitle
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 1 }}
      >
        This is my portfolio
      </Subtitle>
      <ButtonContainer id="button-section">
      <DownloadButton
        href="/assets/Documents/GiacomoLisitaCV_CVE_EN.pdf"
        download="GiacomoLisitaCV.pdf"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, delay: 1.5 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        Download Resume;
      </DownloadButton>
      <EmailButton
          href="mailto:giacomolisita01@gmail.com" // Replace with your email
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 1.5 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Email me;
      </EmailButton>
      </ButtonContainer>
    </HomeSection>
  );
};

export default Work;
