import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as LogoSVG } from '../assets/graphics/logo.svg';
import { ReactComponent as LinkedinSVG } from '../assets/graphics/linkedin.svg';
import { ReactComponent as GithubSVG } from '../assets/graphics/github.svg';

const Nav = styled.nav`
  background: #FFFFFA;
  color: #333;
  padding: 1rem;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: fixed;
  width: 97%;
  z-index: 1000;
`;

const NavList = styled.ul`
  list-style: none;
  padding: 0;
  text-align: right;
  margin-right: 1rem;
`;

const NavItem = styled.li`
  display: inline;
  margin: 0 1rem;
  font-size: 1.2rem;
  font-weight: bold;
`;

const NavLink = styled(Link)`
  color: #000;
  text-decoration: none;
`;

const Logo = styled(Link)`
    margin-left: 1rem;
    svg {
        width: 200px;
        height: 100px;
    }
`;

const LinkedinLogo =  styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }
`;

const GithubLogo = styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }           
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
`;

const Button = styled.a`
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  color: #fff;
  background: ${({ bgColor }) => bgColor};
  border: none;
  cursor: pointer;
  border-radius: 5px;
  text-decoration: none;
  &:hover {
    opacity: 0.8;
  }
`;

showButtons = true;

const Header = ({ showButtons }) => {
  return (
    <Nav>
        <Logo to="/">
            <LogoSVG />
        </Logo>
        <NavList>
            <NavItem><NavLink to="/">Work</NavLink></NavItem>
            <NavItem><NavLink to="/About">About</NavLink></NavItem>
            <NavItem>
                <LinkedinLogo to="https://it.linkedin.com/in/giacomolisita">
                    <LinkedinSVG />
                </LinkedinLogo>
            </NavItem>
            <NavItem>   
                <GithubLogo to="https://github.com/giacolees">
                    <GithubSVG />
                </GithubLogo>
            </NavItem>
        </NavList>
        {showButtons && (
        <ButtonContainer>
          <Button href="/cv.pdf" download="My_CV.pdf" bgColor="#28a745">
            Download CV
          </Button>
          <Button href="mailto:your.email@example.com" bgColor="#dc3545">
            Send Email
          </Button>
        </ButtonContainer>
      )}
    </Nav>
  );
};

export default Header;
