import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Work from './pages/Work';
import About from './pages/About';
import ScrollToTop from './components/ScrollToTop';
// import Projects from './pages/Projects';
// import Contact from './pages/Contact';
// import Footer from './components/Footer';

const Main = styled.main`
  padding-top: 70px; /* Adjust this value to match the height of your header */
`;

const App = () => {
  const [showButtonsInHeader, setShowButtonsInHeader] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const buttonSection = document.getElementById('button-section');
      if (buttonSection) {
        const rect = buttonSection.getBoundingClientRect();
        setShowButtonsInHeader(rect.bottom < 0);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  return (
    <Router>
      <ScrollToTop />
      <Header />
      <Routes>
        <Route path='/' exact element={<Work/>} />
        <Route path='/About' element={<About/>} />
      </Routes>
    </Router>
  );
};

export default App;
