import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Header from './components/Header';
import Work from './pages/Work';
import About from './pages/About';
import ScrollToTop from './components/ScrollToTop';
// import Projects from './pages/Projects';
// import Contact from './pages/Contact';
// import Footer from './components/Footer';

const Main = styled.main`
  padding-top: 10px; /* Adjust this value to match the height of your header */
`;

const App = () => {
  const [showButtonsInHeader, setShowButtonsInHeader] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const buttonSection = document.getElementById('button-section');
      if (buttonSection) {
        const rect = buttonSection.getBoundingClientRect();
        setShowButtonsInHeader(rect.bottom < 0);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <Router>
      <ScrollToTop />
      <Header />
      <Main>
      <Routes>
        <Route path='/' exact element={<Work/>} />
        <Route path='/About' element={<About/>} />
      </Routes>
      </Main>
    </Router>
  );
};

export default App;
