import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const FooterContainer = styled.footer`
  background: #FFFFFA;
  color: #121212;
  padding: 2rem 1rem;
  text-align: center;
  position: relative;
  bottom: 0;
  width: 100%;
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  @media (min-width: 768px) {
    flex-direction: row;
    justify-content: space-between;
  }
`;

const FooterSection = styled.div`
  margin: 1rem 0;
`;

const FooterLink = styled.a`
  color: #fff;
  text-decoration: none;
  margin: 0 1rem;

  &:hover {
    text-decoration: underline;
  }
`;


const LinkedinLogo =  styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }
`;

const GithubLogo = styled(Link)`
    svg {
        width: 25px;
        height: 25px;
    }           
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <FooterSection>
          <p>&copy; 2024 Giacomo Lisita</p>
        </FooterSection>
        <FooterSection>

            <LinkedinLogo to="https://it.linkedin.com/in/giacomolisita">
                    <LinkedinSVG />
            </LinkedinLogo>
        </FooterSection>
      </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
